import * as React from "react";
import { ComponentProps } from "react";
import { adaptive } from "../../../../../@tossteam/colors";
import Txt from "../Text";

export interface Props
  extends Omit<ComponentProps<typeof Txt>, "fontWeight" | "typgraphy"> {
  type?: "default" | "border";
}

function ListRowLeftText({
  type = "default",
  color = adaptive.grey800,
  ...textProps
}: Props) {
  return (
    <div className={`list-row__text list-row__text--type-${type}`}>
      <Txt
        className="list-row__text-content"
        color={color}
        fontWeight={type === "border" ? "regular" : "semibold"}
        typography={type === "border" ? "st9" : "t6"}
        {...textProps}
      />
    </div>
  );
}

export default ListRowLeftText;
