import { storiesOf } from '@storybook/react';
import * as React from 'react';
import { select } from '@storybook/addon-knobs';
import { colors } from '@tossteam/colors';

import { List, ListRow, Txt } from 'src/react';

import { FontWeight } from '../../Text';

storiesOf('ListRow/ðŸ‡¸Texts', module)
  .addDecorator(storyFn => <List>{storyFn()}</List>)
  .add('Basic Usage', () => (
    <List>
      <ListRow contents={<ListRow.Texts type="1RowTypeA" top="1RowTypeA" />} />
      <ListRow contents={<ListRow.Texts type="1RowTypeB" top="1RowTypeB" />} />
      <ListRow contents={<ListRow.Texts type="2RowTypeA" top="2RowTypeA" bottom="bottomText" />} />
      <ListRow contents={<ListRow.Texts type="2RowTypeB" top="2RowTypeB" bottom="bottomText" />} />
      <ListRow contents={<ListRow.Texts type="2RowTypeC" top="2RowTypeC" bottom="bottomText" />} />
      <ListRow contents={<ListRow.Texts type="2RowTypeD" top="2RowTypeD" bottom="bottomText" />} />
      <ListRow contents={<ListRow.Texts type="2RowTypeE" top="2RowTypeE" bottom="bottomText" />} />
      <ListRow contents={<ListRow.Texts type="2RowTypeF" top="2RowTypeF" bottom="bottomText" />} />
      <ListRow
        contents={
          <ListRow.Texts type="3RowTypeA" top="3RowTypeA" middle="middleText" bottom="bottomText" />
        }
      />
      <ListRow
        contents={
          <ListRow.Texts type="3RowTypeB" top="3RowTypeA" middle="middleText" bottom="bottomText" />
        }
      />
    </List>
  ))
  .add('Custom Color, FontWeight', () => {
    const topColor = select(
      'TopColor',
      [colors.grey700, colors.blue500, colors.red500],
      colors.grey700
    );
    const topWeight = select(
      'TopWeight',
      [FontWeight.Bold, FontWeight.Medium, FontWeight.Regular],
      FontWeight.Bold
    );

    return (
      <List>
        <ListRow
          contents={
            <ListRow.Texts
              type="1RowTypeA"
              top={
                <Txt color={topColor} fontWeight={topWeight}>
                  1RowTypeA
                </Txt>
              }
            />
          }
        />
        <ListRow
          contents={
            <ListRow.Texts
              type="2RowTypeA"
              top={
                <Txt color={topColor} fontWeight={topWeight}>
                  1RowTypeA
                </Txt>
              }
              bottom="bottomText"
            />
          }
        />
      </List>
    );
  })
  .add('Right', () => (
    <List>
      <ListRow right={<ListRow.Texts type="Right1RowTypeA" top="Right1RowTypeA" />} />
      <ListRow right={<ListRow.Texts type="Right1RowTypeB" top="Right1RowTypeB" />} />
      <ListRow right={<ListRow.Texts type="Right1RowTypeC" top="Right1RowTypeC" />} />
      <ListRow right={<ListRow.Texts type="Right1RowTypeD" top="Right1RowTypeD" />} />
      <ListRow
        right={<ListRow.Texts type="Right2RowTypeA" top="Right2RowTypeA" bottom="bottomText" />}
      />
      <ListRow
        right={<ListRow.Texts type="Right2RowTypeB" top="Right2RowTypeB" bottom="bottomText" />}
      />
      <ListRow
        right={<ListRow.Texts type="Right2RowTypeC" top="Right2RowTypeC" bottom="bottomText" />}
      />
      <ListRow
        right={<ListRow.Texts type="Right2RowTypeD" top="Right2RowTypeD" bottom="bottomText" />}
      />
      <ListRow
        right={<ListRow.Texts type="Right2RowTypeE" top="Right2RowTypeE" bottom="bottomText" />}
      />
    </List>
  ));
