import { action } from '@storybook/addon-actions';
import { storiesOf } from '@storybook/react';
import * as React from 'react';

import { NPS } from 'src/react';

type NPSProps = React.ComponentProps<typeof NPS>;

function Example() {
  const [value, setValue] = React.useState<NPSProps['value']>(0);

  const onChange = (score: number) => {
    action(`Setting value to ${score}`)(score);
    setValue(score);
  };

  return (
    <div style={{ border: '1px solid rgba(0, 0, 0, 0.08)', padding: '8px' }}>
      <div
        style={{
          padding: '2px',
        }}
      >
        <NPS value={value} onChange={onChange} />
      </div>
    </div>
  );
}

storiesOf('NPS', module)
  .addParameters({})
  .add('ðŸ““example - full', () => {
    return <Example />;
  });
