import classNames from "classnames";
import * as React from "react";
import { ReactNode } from "react";

interface Props {
  className?: string;
  /**
   * @deprecated 'children'을 대신 사용해주세요.
   */
  label?: string;
  href: string;
  children?: ReactNode;
}

export const PostLink = React.memo(
  ({ className, label, href, children = label }: Props) => {
    return (
      <a
        className={classNames(className, "post-link", "font-size--16")}
        href={href}
      >
        {children}
      </a>
    );
  }
);
