import { number } from '@storybook/addon-knobs';
import { storiesOf } from '@storybook/react';
import * as React from 'react';

import { ProgressBar } from 'src/react';

type ProgressBarProps = React.ComponentProps<typeof ProgressBar>;

storiesOf('ProgressBar', module)
  .addParameters({})
  .add('Example', () => {
    const sizes: Array<Exclude<ProgressBarProps['size'], undefined>> = ['light', 'normal', 'bold'];
    const progress = number('progress (0-1)', 0.5);

    return (
      <>
        {sizes.map((size, index) => (
          <div style={{ border: '1px solid rgba(0, 0, 0, 0.08)', padding: '8px' }} key={index}>
            <div
              style={{
                padding: '2px',
              }}
            >
              {size}
              <ProgressBar size={size} progress={progress} />
              <br />
            </div>
          </div>
        ))}
      </>
    );
  })
  .add('Animate', () => {
    return (
      <div style={{ padding: '24px' }}>
        <ProgressBar progress={0.9} animate={true} />
      </div>
    );
  });
