import { action } from '@storybook/addon-actions';
import { storiesOf } from '@storybook/react';
import * as React from 'react';
import { colors } from '@tossteam/colors';

import { Slider } from 'src/react';

import Readme from '../README.md';
import Tooltip from '../../Tooltip';

storiesOf('Slider', module)
  .addParameters({ readme: { sidebar: Readme } })
  .add('📓example - full', () => {
    const minValue = 100;
    const maxValue = 200;
    const formatLabel = (value: number) => `${value} 만원`;

    function SliderExample() {
      const [value, setValue] = React.useState(65);

      function onChange(event: React.ChangeEvent<HTMLInputElement>) {
        setValue(parseInt(event.target.value, 10));
        action('onChange - Slider')(event);
      }

      return (
        <Slider
          value={value}
          onChange={onChange}
          minValue={minValue}
          maxValue={maxValue}
          label={{ min: formatLabel(minValue), max: formatLabel(maxValue) }}
          tooltip={<Tooltip />}
          color={colors.greyOpacity500}
        />
      );
    }

    return (
      <>
        <br />
        <br />
        <br />

        <SliderExample />
      </>
    );
  });
