import * as React from 'react';
import cx from 'classnames';

interface Props extends React.LiHTMLAttributes<HTMLLIElement> {
  left: React.ReactNode;
  right: React.ReactNode;
  leftRatio?: number;
}

function BaseTableRow({ left, leftRatio, right, className, ...rest }: Props) {
  return (
    <li {...rest} className={cx('table-row', 'font-size--16', className)}>
      <div
        className="table-row__label"
        style={leftRatio !== undefined ? { width: `${leftRatio}%` } : undefined}
      >
        {left}
      </div>
      <div className="table-row__content">{right}</div>
    </li>
  );
}

export default BaseTableRow;
