import { action } from '@storybook/addon-actions';
import { storiesOf } from '@storybook/react';
import * as React from 'react';

import { Form, TextFieldBig } from 'src/react';

import Readme from '../README.md';

storiesOf('TextFieldBig', module)
  .addParameters({ readme: { sidebar: Readme } })
  .add('📓example - full', () => {
    function TextFieldBigFullExample() {
      const [amount, setAmount] = React.useState('');

      function onChangeAmount(event: React.ChangeEvent<HTMLInputElement>) {
        setAmount(event.target.value);
        action('onChange - TextFieldBig - 금액')(...arguments);
      }

      return (
        <Form>
          <Form.Group label="금액">
            <TextFieldBig
              type="tel"
              placeholder="플레이스홀더"
              suffix="만원"
              value={amount}
              onChange={onChangeAmount}
            />
          </Form.Group>
        </Form>
      );
    }

    return <TextFieldBigFullExample />;
  });
