import { action } from '@storybook/addon-actions';
import { storiesOf } from '@storybook/react';
import * as React from 'react';

import { TextFieldLine } from 'src/react';

import Readme from '../README.md';

storiesOf('TextFieldLine', module)
  .addParameters({ readme: { sidebar: Readme } })
  .add('value & onChange', () => {
    function TextFieldLineValueOnChangeExample() {
      const [value, setValue] = React.useState('');

      function onChange(event: React.ChangeEvent<HTMLInputElement>) {
        setValue(event.target.value);
        action('onChange - TextFieldLine')(...arguments);
      }

      return <TextFieldLine label="레이블" value={value} onChange={onChange} />;
    }

    return <TextFieldLineValueOnChangeExample />;
  });
