import classnames from 'classnames';
import * as React from 'react';

import formatCurrency from '../../utils/formatCurrency';
import Top, { Props as TopProps } from './Top';

export interface Props extends TopProps {
  value?: number;
  suffix?: string;
}

export default class Top01 extends React.PureComponent<Props> {
  public render() {
    const props = this.props;
    return (
      <Top
        className={classnames('top-01', 'typography-t1', props.className)}
        style={props.style}
        horizontalPadding={props.horizontalPadding}
        topPadding={props.topPadding}
        bottomPadding={props.bottomPadding}
        wordBreak={props.wordBreak}
      >
        {props.value !== undefined ? (
          <>
            {formatCurrency(props.value)}
            <span className="top-01__suffix">{props.suffix}</span>
          </>
        ) : (
          props.children
        )}
      </Top>
    );
  }
}
