import classnames from 'classnames';
import * as React from 'react';

import convertNewLineToJSX from '../../utils/convertNewLineToJSX';
import Top, { Props as TopProps } from './Top';

export interface Props extends TopProps {
  title?: string;
}

export default class Top02 extends React.PureComponent<Props> {
  public render() {
    const props = this.props;
    return (
      <Top
        className={classnames('top-02', 'typography-t2', props.className)}
        style={props.style}
        horizontalPadding={props.horizontalPadding}
        topPadding={props.topPadding}
        bottomPadding={props.bottomPadding}
        wordBreak={props.wordBreak}
      >
        {props.title !== undefined ? convertNewLineToJSX(props.title) : props.children}
      </Top>
    );
  }
}
