// e.g. http://jsfiddle.net/hAfMM/9571/
// https://stackoverflow.com/a/14428340
export default function(someNumber: number, fractionDigits?: number) {
  let fixedNumberString = String(someNumber);
  if (fractionDigits !== undefined) {
    fixedNumberString = someNumber.toFixed(fractionDigits);
  }
  return fixedNumberString.replace(/./g, (c: string, i: number, a: string) => {
    return i > 0 && c !== '.' && (a.length - i) % 3 === 0 ? `,${c}` : c;
  });
}
