import { useEffect } from 'react';

import isSafeAreaExists from '../isSafeAreaExists';

export const androidSafeAreaPatchCssClass = 'android-safe-area';

export default function useSafeAreaPatch() {
  useEffect(() => {
    if (!isSafeAreaExists()) {
      document?.body?.classList?.add?.(androidSafeAreaPatchCssClass);
    }
  }, []);
}
