import styled from '@emotion/styled';
import { addPropertyControls } from 'framer';
import * as React from 'react';
import * as packageInfo from '../package.json';
import { makeDisplayName } from '../src/utils/makeDisplayName';

interface Props {}

export function PackageInfo({}: Props) {
  return (
    <Container>
      <h1>PackageInfo</h1>
      <h2>
        - {packageInfo.framer.displayName}@{packageInfo.version}
      </h2>
    </Container>
  );
}

addPropertyControls(PackageInfo, {});

(PackageInfo as any).defaultProps = {
  width: 214,
  height: 116,
};

PackageInfo.displayName = makeDisplayName('tool')('PackageInfo', '패키지정보');

const Container = styled.div`
  background-color: white;
  padding: 8px;
`;
