import { colors } from '../../@tossteam/colors';
import { colors as canvasColors } from '../../code/canvas';

export const colorNameByHexCode: {
  [key: string]: keyof typeof colors;
} = Object.entries(colors).reduce(
  (previousValue, [key, value]) => ({
    ...previousValue,
    [value.toLowerCase()]: key,
  }),
  {}
);

export const colorNameByCanvasColorValue: {
  [key: string]: keyof typeof canvasColors;
} = Object.entries(canvasColors).reduce(
  (previousValue, [key, value]) => ({
    ...previousValue,
    [value]: key.match(/^\w+/)[0],
  }),
  {}
);
